  if (data.users[odUserIdStr]) return res.json({ success: false, error: 'exists', message: 'Ð’Ñ‹ ÑƒÐ¶Ðµ Ð·Ð°Ñ€ÐµÐ³Ð¸ÑÑ‚Ñ€Ð¸Ñ€Ð¾Ð²Ð°Ð½Ñ‹' });
  const partnerId = data.pairCodes[codeUpper];
